import React,{useState} from 'react'
import uniqid from 'uniqid';

export default function ListadoNombres(){

    const [nombre,setNombre] = useState("")
    const [listaNombres,setListaNombres] =useState([])

    const addNombre = (e) =>{
        e.preventDefault()
        const nuevoNombre ={
            id:uniqid(),
            tituloNombre:nombre
        }
        //con esto lo que hago es decirle que lo coloque al final de la lista de nombres al nombre
        setListaNombres([nuevoNombre,...listaNombres])
    }

    return(
        <div>
            <h2>Aplicacion de CRUD basica </h2>
            <div className="row">
                <div className="col">
                    <h2>Listado de nombres</h2>
                    <ul className="list-group">
                        {
                            listaNombres.map(item =>{

                                <li key={item.id} className="list-group-item">{item.tituloNombre} </li>
                            })
                        }
                    </ul>
                </div>
                <div className="col">
                    <h2>Formulario para a√±adir nombres </h2>
                    <form onSubmit ={(e)=>{addNombre(e)}} className="form-group"> 
                        <input onChange={(e)=>{setNombre(e.target.value)}} className="form-control mb-3" type="text" placeholder="Introduce el nombre"></input>
                        <input className="btn btn-info btn-block" type="submit" value="Registrar Nombre"/>
                    </form>
                </div>
            </div>
        </div>
    )
}
