import React,{useState} from 'react';
//importe auth de firebase
import {auth} from '../firebaseconfig';
export default function Login(){
    const [email,setEmail] = useState("")
    const [pass,setPass] = useState("")
    //creo el estado para tomar los errores y mostrarlos
    const [msgerror, setMgError] = useState(null)

    const RegistrarUsuario = (e) =>{
        e.preventDefault();
        try{
            auth.createUserWithEmailAndPassword(email,pass)
            //con esto lo que hago es evitar que sale siempre el alert por eso recibo la respuesta
            .then( r => alert("Usuario Registrado"))
            
        }catch(e){
            if(e.code =='auth/invalid-email'){
                setMgError("Formato de mail incorrecto")
            }
            if(e.code == "auth/weak-password"){
                setMgError("La contraseña es muy corta, debe tener minimo 6 caracteres o más")
            }
        }
    }
    return(
        <div className="row mt-5">
            <div className="col"></div>
            <div className="col">
                <form onSubmit={RegistrarUsuario} className="form-group">
                    <input 
                    onChange ={(e)=>{setEmail(e.target.value)}} 
                    className="form-control" type="text" 
                    placeholder="introduce el email"
                    />
                    <input
                    onChange ={(e)=>{setPass(e.target.value)}}
                    className="form-control mt-4" 
                    type="password" 
                    placeholder="introduce la contraseña"
                    />
                    <input type="submit" value="Registrar Usuario" className ="btn btn-dark btn-block mt-4"/>
                </form>
                {
                    msgerror ? (<div>{msgerror}</div>) : (<span></span>)
                }
            </div>
            <div className="col"></div>
        </div>
    )
}
